/* eslint-disable no-debugger */
import axiosInstance from '../../api'
import axios from 'axios'

import * as types from './types'

import products from './mock'

export const listProducts = () => async (dispatch) => {
  try {
    dispatch({ type: types.PRODUCT_LIST_REQUEST })
    const response = await axios.get(`/products/find-all`)

    dispatch({
      type: types.PRODUCT_LIST_SUCCESS,
      payload: response.data,
    })
  } catch (error) {
    dispatch({
      type: types.PRODUCT_LIST_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    })
  }
}

export const listProductDetails = (id) => async (dispatch) => {
  try {
    dispatch({ type: types.PRODUCT_DETAILS_REQUEST })

    const response = await axios.get(`/products/find-one/${id}`)

    dispatch({
      type: types.PRODUCT_DETAILS_SUCCESS,
      payload: response.data,
    })
  } catch (error) {
    dispatch({
      type: types.PRODUCT_DETAILS_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    })
  }
}

// create new product
export const productCreate = (payload) => async (dispatch) => {
  try {
    dispatch({ type: types.PRODUCT_CREATE_REQUEST })

    const response = await axios.post(`/products/create`, payload)

    dispatch({
      type: types.PRODUCT_CREATE_SUCCESS,
      payload: response.data,
    })
  } catch (error) {
    dispatch({
      type: types.PRODUCT_CREATE_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    })
  }
}

export const productUpdate = (id, payload) => async (dispatch) => {
  try {
    dispatch({ type: types.PRODUCT_UPDATE_REQUEST })
    const response = await axios.patch(`/products/update/${id}`, payload)
    dispatch({
      type: types.PRODUCT_UPDATE_SUCCESS,
      payload: response.data,
    })
  } catch (error) {
    dispatch({
      type: types.PRODUCT_UPDATE_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    })
  }
}

export const productUploadImage = (id, formData) => async (dispatch) => {
  try {
    dispatch({ type: types.UPLOAD_IMAGE_REQUEST })

    const response = await axiosInstance().post(
      `/products/upload-file/${id}`,
      formData
    )

    dispatch({
      type: types.UPLOAD_IMAGE_SUCCESS,
      payload: response.data,
    })
  } catch (error) {
    dispatch({
      type: types.UPLOAD_IMAGE_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    })
  }
}
