/* eslint-disable no-debugger */
import React from 'react'
import useReactRouter from 'use-react-router'
import { Row, Table, message, Col, Modal } from 'antd'
import {
  DeleteOutlined,
  EditOutlined,
  ExclamationCircleOutlined,
} from '@ant-design/icons'
import { PAGE_SIZE } from '../../../utils/constants'
import { employeeEditPage } from '../../../routes/urls'

const ListTable = (props) => {
  const { history } = useReactRouter()
  const onEdit = (employee) => {
    const id = props.employee._id
    console.log('employee', employee)
 
    history.push({ pathname: employeeEditPage(id), state: { view: false } })
  }
  const onDelete = (employee) => {
    console.log('employee', employee)
  }
  const columns = [
    {
      title: 'First name',
      dataIndex: 'firstName',
      key: 'firstName',
      sorter: (a, b) => a.firstName.length - b.firstName.length,
      sortDirections: ['descend'],
    },
    {
      title: 'Last name',
      dataIndex: 'lastName',
      key: 'lastName',
      sorter: (a, b) => a.lastName.length - b.lastName.length,
      sortDirections: ['descend'],
    },

    {
      title: 'Email address ',
      dataIndex: 'email',
      key: 'email',
    },
    {
      title: 'Phone number',
      dataIndex: 'tel',
      key: 'tel',
    },
    {
      title: 'Gender',
      dataIndex: 'gender',
      key: 'gender',
      filters: [
        {
          text: 'Male',
          value: 'Male',
        },
        {
          text: 'Female',
          value: 'Female',
        },
      ],
      onFilter: (value, record) => record.gender.indexOf(value) === 0,
    },
    {
      title: 'Action',
      key: '',
      align: 'center',
      render(employee) {
        return (
          <Row justify="space-around">
            <Col span={12} justify="center">
              <EditOutlined onClick={() => onEdit(employee)} />
            </Col>
            <Col span={12} justify="center">
              <DeleteOutlined onClick={() => onDelete(employee)} />
            </Col>
          </Row>
        )
      },
    },
  ]

  return (
    <Table
      bordered
      rowKey="_id"
      columns={columns}
      dataSource={props.employees}
      pagination={{ defaultPageSize: PAGE_SIZE }}
    />
  )
}
export default ListTable
