import React from 'react'
import { Card, Rate, Button, Row, Col } from 'antd'
import { HeartOutlined } from '@ant-design/icons'
import { Link } from 'react-router-dom'
import { OmitProps } from 'antd/lib/transfer/ListBody'

const { Meta } = Card

const ProductPage = ({ product, onMarked, props }) => {
  // const data = product
  const description = (
    <Row>
      <Col span={24}>
        <h3>$ {product.price}</h3>
      </Col>
      <Col span={24}>
        {' '}
        <Rate disabled allowHalf defaultValue={product.rating} />
      </Col>
      <Col span={24}>
        {' '}
        <p>{product.category}</p>
      </Col>
    </Row>
  )
  // const extra = (
  //   <div>
  //     <h4>{product.numReviews}</h4>
  //   </div>
  // )
  return (
    <Card
      className="card-container"
      hoverable
      style={{ width: '100%' }}
      // extra={extra}
      cover={<img alt="example" src={product.image} />}
    >
      <div className="onMarked-container">
        {' '}
        <Rate
          className="onMarked"
          count={1}
          onChange={onMarked}
          character={<HeartOutlined />}
        />
      </div>
      <Meta title={product.name} description={description} />
      <Row>
        <Col span={12}>
          <Button className="white-btn">Add to Cart</Button>
        </Col>
        <Col span={12}>
          <Button
            className="white-btn"
            onClick={() =>
              props.history.push(`/productView/${product.name}`, { product })
            }
          >
            View More
          </Button>
        </Col>
      </Row>
    </Card>
  )
}

export default ProductPage
