/* eslint-disable no-debugger */
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Col, Row, Layout, Upload, Icon, Modal, message, Button } from 'antd'
import LeftNav from './LeftNav'
import { BASE_IMG_URL } from '../../utils/constants'
import {
  productUploadImage,
  listProductDetails,
} from '../../../state/ducks/products/actions'
const { Sider, Content, Header, Footer } = Layout

function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader()
    reader.readAsDataURL(file)
    reader.onload = () => resolve(reader.result)
    reader.onerror = (error) => reject(error)
  })
}
const UploadImage = (props) => {
  const dispatch = useDispatch()
  const productImage = useSelector(
    (state) => state.productOperations.productImage
  )
  const productDetails = useSelector(
    (state) => state.productOperations.productDetails
  )

  console.log('1', productImage)
  console.log('2', productDetails)
  const [fileList, setFileList] = useState([
    // {
    //   uid: '-1',
    //   name: 'image.png',
    //   status: 'done',
    //   url: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
    // },
  ])
  // 接收传入的imgs
  const [imgs, setImgs] = useState([])
  const [previewVisible, setPreviewVisible] = useState(false)
  const [previewImage, setPreviewImage] = useState('')
  const [previewTitle, setPreviewTitle] = useState('')

  const handleChange = ({ file, fileList }) => {
    setFileList(fileList)
  }
  const handleCancel = () => setPreviewVisible(false)
  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj)
    }

    setPreviewImage(file.url || file.preview)
    setPreviewVisible(true)
    setPreviewTitle(
      file.name || file.url.substring(file.url.lastIndexOf('/') + 1)
    )
  }

  useEffect(() => {
    debugger
    const id = props.location.state ? props.location.state.record._id : ''
    dispatch(listProductDetails(id))
  }, [])
  useEffect(() => {
    let fileList = []
    if (imgs && imgs.length > 0) {
      fileList = imgs.map((img, index) => ({
        uid: -index,
        name: img,
        status: 'done',
        url: BASE_IMG_URL + img,
      }))
    }
    setFileList(fileList)
  }, [imgs])
  const beforeUpload = (file) => {
    return false
  }
  const onSubmit = () => {
    const id = props.location.state ? props.location.state.record._id : ''
    let formData = new FormData()

    formData.append('image', fileList[0].originFileObj)
    dispatch(productUploadImage(id, formData))
  }
  return (
    <Layout>
      <Header theme="light" style={{ backgroundColor: '#fff' }}>
        {' '}
        <Link to={'/manage-services'}>Back to Listings</Link>
      </Header>
      <Layout>
        <Sider theme="light" className="product-menu">
          <LeftNav
            productData={
              props.location.state ? props.location.state.record : ''
            }
            id={props.location.state ? props.location.state.record._id : ''}
          />
        </Sider>
        <Content className="edit-container">
          <Row>
            <Upload
              accept="image/*"
              name="image"
              listType="picture-card"
              fileList={fileList}
              onChange={handleChange}
              onPreview={handlePreview}
              beforeUpload={beforeUpload}
            >
              {fileList.length < 1 && '+ Upload'}
            </Upload>
            <Modal
              visible={previewVisible}
              footer={null}
              onCancel={handleCancel}
            >
              <img alt="example" style={{ width: '100%' }} src={previewImage} />
            </Modal>
          </Row>
          <Row justify="center">
            <img
              alt="example"
              style={{ width: '20%' }}
              src="http://localhost:5000/uploads/productImgs/9e469c3677d5cb22bcef10c72c06ac4-4e4c9f6691db.jpg"
            />
          </Row>
          <Row justify="end">
            <Col span={4}>
              <Button type="primary" htmlType="submit" onClick={onSubmit}>
                Submit
              </Button>
            </Col>
          </Row>
        </Content>
      </Layout>
    </Layout>
  )
}
export default UploadImage
