/* eslint-disable no-debugger */
import React, { useEffect, useState } from 'react'
import { Card, Row, Form } from 'antd'
import { BackwardOutlined } from '@ant-design/icons'

const EmployeeDetail = (props) => {
  const [employeeId, setEmployeeId] = useState(null)
  const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      lg: { span: 4 },
    },
    wrapperCol: {
      xs: { span: 24 },
      lg: { span: 10 },
    },
  };
  const [form] = Form.useForm();
  useEffect(() => {
    const style = props.location.state.style
    const id = props.location.state._id
    if (style === 'edit') {
      setEmployeeId(id)
    }
  }, [props])
  const onBack = () => {
    props.history.goBack()
  }
  const title = (
    <>
      <Row>
        <BackwardOutlined onClick={onBack} /> <span>Back to list page</span>
      </Row>
    </>
  )
  return (
    <>
      <h2>EmployeeDetail</h2>
      <Card title={title} {...formItemLayout} form={form}>
        <Form></Form>
      </Card>
    </>
  )
}
export default EmployeeDetail
