import * as types from './types'
import { message } from 'antd'
export const productListReducer = (state = { employees: [] }, action) => {
  switch (action.type) {
    case types.GET_ALL_EMPLOYEE_REQUEST:
      return { loading: true, employees: [] }
    case types.GET_ALL_EMPLOYEE_SUCCESS:
      return {
        loading: false,
        employees: action.payload,
      }
    case types.GET_ALL_EMPLOYEE_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export const employeeAddReducer = (state = {}, action) => {
  switch (action.type) {
    case types.EMPLOYEE_ADD_REQUEST:
      return { loading: true }
    case types.EMPLOYEE_ADD_SUCCESS:
      message.success('Create new listing success!')
      return { loading: false, data: action.payload }
    case types.EMPLOYEE_ADD_FAIL:
      message.error(action.payload)
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export const productUpdateReducer = (state = {}, action) => {
  switch (action.type) {
    case types.PRODUCT_UPDATE_REQUEST:
      return { loading: true }
    case types.PRODUCT_UPDATE_SUCCESS:
      message.success('Update listing success!')
      return { loading: false, data: action.payload }
    case types.PRODUCT_UPDATE_FAIL:
      message.error(action.payload)
      return { loading: false, error: action.payload }
    default:
      return state
  }
}
export const productUploadImageReducer = (state = {}, action) => {
  switch (action.type) {
    case types.UPLOAD_IMAGE_REQUEST:
      return { loading: true }
    case types.UPLOAD_IMAGE_SUCCESS:
      message.success('Upload Image success!')
      return { loading: false, data: action.payload }
    case types.UPLOAD_IMAGE_FAIL:
      message.error(action.payload)
      return { loading: false, error: action.payload }
    default:
      return state
  }
}
