import React, { useEffect, useState } from 'react'
import { Redirect, Link } from 'react-router-dom'
import { Col, Row, Layout, Switch } from 'antd'
import { ArrowLeftOutlined } from '@ant-design/icons'
import LeftNav from './LeftNav'
import BasicInfo from './BasicInfo'
const { Sider, Content, Header, Footer } = Layout
const UpdateListing = (props) => {
  const [status, setStatus] = useState(
    props.location.state ? props.location.state.record.status : 'unlist'
  )
  const [productData, setProductData] = useState(null)
  const [id, setId] = useState(props.id)
  const onChange = (value) => {
    setStatus(value ? 'listed' : 'unlist')
  }
  useEffect(() => {
    const data = props.location.state ? props.location.state.record : {}
    const id = props.location.state ? props.location.state.record._id : ''
    const status = props.location.state
      ? props.location.state.record.status
      : 'unlist'
    setProductData(data)
    setStatus(status)
    setId(id)
  }, [props])
  return (
    <Layout>
      <Header
        theme="light"
        style={{ backgroundColor: '#fff' }}
        className="product-header"
      >
        {' '}
        <Row justify="space-around" align="middle ">
          <Col span={10}>
            <ArrowLeftOutlined />
            <Link to={'/manage-services'}> Back to Listings</Link>
          </Col>
          <Col span={14}>
            {' '}
            <Row justify="end" align="middle ">
              <Col
                span={10}
                style={{ textAlign: 'right', paddingRight: '10px' }}
              >
                <h3>List</h3>
              </Col>
              <Col span={8} style={{ flex: '0' }}>
                <Switch
                  onChange={onChange}
                  defaultChecked={status === 'listed' ? true : false}
                />
              </Col>
            </Row>
          </Col>
        </Row>
      </Header>
      <Layout>
        <Sider theme="light" className="product-menu">
          <LeftNav
            productData={
              props.location.state ? props.location.state.record : ''
            }
            id={props.location.state ? props.location.state.record._id : ''}
          />
        </Sider>
        <Content className="edit-container">
          {' '}
          <BasicInfo
            productData={
              props.location.state ? props.location.state.record : ''
            }
            id={props.location.state ? props.location.state.record._id : ''}
            status={status}
            editStatus="1"
          />
        </Content>
      </Layout>
    </Layout>
  )
}
export default UpdateListing
