/* eslint-disable no-debugger */
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { signUp, login } from '../../../state/ducks/user/actions'
import { Form, Input, Button } from 'antd'
import { UserOutlined, LockOutlined } from '@ant-design/icons'
const { Item } = Form
const SignUpForm = (props) => {
  const dispatch = useDispatch()
  debugger
  const userSignIn = useSelector((state) => state.userOperations.userCreate)
  const [user, setUser] = useState({})

  useEffect(() => {
    if (userSignIn && userSignIn.success === 'success') {
      dispatch(login(user))
      props.closeSignUp()
    }
  }, [dispatch, props, user, userSignIn])
  const [next, setNext] = useState(false)
  const [email, setEmail] = useState('')
  const onFinish = (event) => {
    if (event.email) {
      const { email } = event
      setNext(!next)
      setEmail(email)
    }
    const payload = {}
    ;(payload.email = email),
      (payload.firstName = event.firstName),
      (payload.lastName = event.lastName),
      (payload.password = event.password)
    if (payload.email && payload.firstName && payload.password) {
      setUser(payload)
      dispatch(signUp(payload))
    }
  }

  return (
    <Form
      name="normal_logup"
      className="login-form"
      initialValues={{ remember: true }}
      onFinish={onFinish}
    >
      {' '}
      <Item
      // name="googleLink"
      >
        <button
          className="google_link"
          style={{}}
          onClick={() => alert('Continue With Google')}
        >
          {' '}
          <img src="/images/google.jpg" alt="Google" />
          <p>Continue With Google</p>
        </button>
        <div className="form-separator">
          <span>OR</span>
        </div>
      </Item>
      {!next && (
        <Item
          name="email"
          rules={[
            {
              required: true,
              whitespace: true,
              message: 'Please input your email!',
            },
          ]}
        >
          <Input
            prefix={<UserOutlined className="site-form-item-icon" />}
            placeholder="Email"
          />
        </Item>
      )}
      {next && (
        <Item
          name="firstName"
          rules={[{ required: true, message: 'Please input your first name!' }]}
        >
          <Input
            prefix={<UserOutlined className="site-form-item-icon" />}
            placeholder="Please input your Username"
          />
        </Item>
      )}
      {next && (
        <Item
          name="lastName"
          rules={[{ required: true, message: 'Please input your last name!' }]}
        >
          <Input
            prefix={<UserOutlined className="site-form-item-icon" />}
            placeholder="Please input your Username"
          />
        </Item>
      )}
      {next && (
        <Item
          name="password"
          rules={[
            {
              required: true,
              whitespace: true,
              message: 'Please input your Password!',
            },
          ]}
        >
          <Input
            prefix={<LockOutlined className="site-form-item-icon" />}
            type="password"
            placeholder="Please input your Password!"
          />
        </Item>
      )}
      {next ? (
        <Item>
          <Button
            type="primary"
            htmlType="submit"
            className="login-form-button join"
          >
            Join
          </Button>
        </Item>
      ) : (
        <Item>
          <Button
            type="primary"
            htmlType="submit"
            className="login-form-button"
          >
            Continue
          </Button>
        </Item>
      )}
      {next && (
        <Item className="footer-text smallSize">
          <a
            style={{
              display: 'block',
              textAlign: 'center',
            }}
            onClick={() => setNext(false)}
          >
            {' '}
            Back to prev form
          </a>
          <p>
            By joining, you agree to Fiverrâ€™s
            <a onClick={() => alert('go to Terms of Service page')}>
              Terms of Service
            </a>
            <br />
            as well as to receive occasional emails from us.
          </p>
        </Item>
      )}
      {!next && (
        <Item className="footer-text">
          <p>By joining I agree to receive emails from HOMESERV</p>
        </Item>
      )}
      <Item className="footer-signUp">
        <p>Already a member?</p>
        <button
          className="link-button"
          onClick={() => {
            props.showLogin()
            props.closeSignUp()
            setNext(false)
          }}
        >
          Sign In
        </button>
      </Item>
    </Form>
  )
}

export default SignUpForm
