import React, { useEffect, useState } from 'react'
import { withRouter } from 'react-router-dom'
import { Redirect } from 'react-router-dom'
import { Col, Row, Menu } from 'antd'
import {
  AppstoreOutlined,
  MailOutlined,
  SettingOutlined,
} from '@ant-design/icons'

const { SubMenu } = Menu
// submenu keys of first level
const rootSubmenuKeys = ['sub1', 'sub2', 'sub4']

const LeftNav = (props) => {
  const [openKeys, setOpenKeys] = useState(['sub1'])
  const [record, setRecord] = useState(
    props.productData ? props.productData : {}
  )
  useEffect(() => {
    const productData = props.productData ? props.productData : {}
    setRecord(productData)
    // console.log('record in leftNav', record)
  }, [props])

  const onOpenChange = (keys) => {
    const latestOpenKey = keys.find((key) => openKeys.indexOf(key) === -1)
    if (rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
      setOpenKeys(keys)
    } else {
      setOpenKeys(latestOpenKey ? [latestOpenKey] : [])
    }
  }
  return (
    <Menu
      mode="inline"
      openKeys={openKeys}
      onOpenChange={onOpenChange}
      style={{ width: 256 }}
    >
      <Menu.Item key="1">
        <a
          onClick={
            // (() => props.history.push('/product/update-listing'),
            // { record: props.productData ? props.productData : {} })

            () => {
              props.history.push('/product/update-details', { record })
              // props.history.push('/product/upload-image')
            }
          }
        >
          {' '}
          Basic Info
        </a>
      </Menu.Item>
      <SubMenu key="sub4" title="Details">
        <Menu.Item key="2">
          <a
            onClick={() =>
              props.history.push('/product/update-description', { record })
            }
          >
            Description
          </a>
        </Menu.Item>
        <Menu.Item key="3">
          {' '}
          <a
            onClick={() =>
              props.history.push('/product/update-faq', { record })
            }
          >
            FQA
          </a>
        </Menu.Item>
        <Menu.Item key="4">
          {' '}
          <a
            onClick={() =>
              props.history.push('/product/update-requirements', { record })
            }
          >
            Requirements
          </a>
        </Menu.Item>
      </SubMenu>
      <Menu.Item key="5">
        <a
          onClick={() =>
            props.history.push('/product/upload-image', { record })
          }
        >
          Image
        </a>
      </Menu.Item>
      <Menu.Item key="6">Policy&Rule</Menu.Item>
    </Menu>
  )
}
export default withRouter(LeftNav)
