import {
  LOG_IN_REQUEST,
  LOG_IN_SUCCESS,
  LOG_IN_FAIL,
  SIGN_UP_REQUEST,
  SIGN_UP_SUCCESS,
  SIGN_UP_FAIL,
  USER_PERSONAL_INFO_REQUEST,
  USER_PERSONAL_INFO_SUCCESS,
  USER_PERSONAL_INFO_FAIL,
} from './types'
import { message } from 'antd'

export const signUpReducer = (state = {}, action) => {
  switch (action.type) {
    case SIGN_UP_REQUEST:
      return { loading: true }
    case SIGN_UP_SUCCESS:
      message.success('Thank you for join us !')
      return {
        loading: false,
        success: 'success',
        userInfo: action.payload,
      }
    case SIGN_UP_FAIL:
      message.error(action.payload)
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export const userLogoutReducer = (state = {}, action) => {
  switch (action.type) {
    case SIGN_UP_REQUEST:
      return { loading: true }
    case SIGN_UP_SUCCESS:
      // message.success('Log out success , see you !')
      return {
        loading: false,
        success: 'success',
        // userInfo: undefined,
      }
    case SIGN_UP_FAIL:
      message.error(action.payload)
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export const loginReducer = (state = {}, action) => {
  switch (action.type) {
    case LOG_IN_REQUEST:
      return { loading: true }
    case LOG_IN_SUCCESS:
      message.success('Log in success , welcome to HOMESERV !')
      return {
        loading: false,
        success: 'success',
        userInfo: action.payload,
      }
    case LOG_IN_FAIL:
      message.error(action.payload)
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export const userPersonalInfoReducer = (state = { user: {} }, action) => {
  switch (action.type) {
    case USER_PERSONAL_INFO_REQUEST:
      return { loading: true }
    case USER_PERSONAL_INFO_SUCCESS:
      return {
        loading: false,
        success: 'success',
        user: action.payload,
      }
    case USER_PERSONAL_INFO_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}
