import React, { useEffect, useState } from 'react'

import { useDispatch, useSelector } from 'react-redux'
import { Input, Col, Row, Select } from 'antd'

import { listProducts } from '../../state/ducks/products/actions'
import Loader from '../common/loader/Loader'
import Message from '../common/Message'
import ProductPage from '../features/ProductPage'
import { AudioOutlined } from '@ant-design/icons'

const { Option } = Select
const { Search } = Input
const ProductList = (props) => {
  const { title } = props.location.state ? props.location.state.item : ''
  // const keyword = match.params.keyword
  const [selValue, setSelValue] = useState('1')
  const [sortValue, sortSortValue] = useState('1')

  const dispatch = useDispatch()
  const productList = useSelector(
    (state) => state.productOperations.productList
  )
  const { loading, error, products } = productList
  const onMarked = (value) => {
    // value:1
    // console.log('onMarked', value)
  }
  const onSearch = (value) => {
    // console.log('onSearch', value)
    // console.log('selValue', selValue)
  }
  const onSelect = (value) => {
    // console.log('onSelect', value)
    setSelValue(value)
  }
  const onSort = (value) => {
    // console.log('onSort', value)
    sortSortValue(value)
  }

  useEffect(() => {
    dispatch(listProducts())
  }, [dispatch])

  const suffix = (
    <AudioOutlined
      style={{
        fontSize: 16,
        color: '#1890ff',
      }}
    />
  )

  return (
    <section>
      <h2>{title}</h2>
      <Row justify="start" align="middle ">
        <Col span={8}>
          {' '}
          <Search
            placeholder="input search text"
            onSearch={onSearch}
            enterButton
            size="large"
          />
        </Col>
        <Col span={8}>
          <Select
            defaultValue="1"
            size="large"
            style={{ width: 200 }}
            onChange={onSelect}
          >
            <Option value="1">Search by name</Option>
            <Option value="2">Search by description</Option>
          </Select>
        </Col>
      </Row>
      <br />
      <Row justify="space-between" align="middle ">
        <Col span={8}>
          <h3>123 Vendors available</h3>
        </Col>
        <Col span={8}>
          <Row justify="end">
            <Col span={6}>
              <h3>Sort byï¼š</h3>
            </Col>
            <Col span={17}>
              <Select
                defaultValue="1"
                size="large"
                style={{ width: '99% ' }}
                onChange={onSort}
              >
                <Option value="1">Recommended</Option>
                <Option value="2">Sales</Option>
                <Option value="3">Rate</Option>
                <Option value="4">Price</Option>
              </Select>
            </Col>
          </Row>
        </Col>
      </Row>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div
          className="site-card-wrapper"
          style={{ marginTop: '20px', marginBottom: '20px' }}
        >
          <Row gutter={16}>
            {products.map((product) => (
              <Col
                key={product._id}
                span={6}
                className="product-container"
                style={{
                  width: '100%',
                }}
              >
                <ProductPage
                  product={product}
                  onMarked={onMarked}
                  props={props}
                />
              </Col>
            ))}
          </Row>
        </div>
      )}
      <Row justify="space-between" align="middle ">
        <Col span={24}>
          <button className="primary-btn">View More</button>
        </Col>
      </Row>
    </section>
  )
}

export default ProductList
