/* eslint-disable no-undef */
/* eslint-disable no-debugger */
import React, { useEffect, useState } from 'react'
import { withRouter } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Form, Input, Button, Select, Row, Col } from 'antd'
import {
  productCreate,
  productUpdate,
} from '../../../state/ducks/products/actions'
import { name } from 'store/storages/cookieStorage'
const { Option } = Select
const { Item } = Form

const BasicInfo = (props) => {
  const [form] = Form.useForm()
  const dispatch = useDispatch()
  const [productData, setProductData] = useState(
    props.productData ? props.productData : {}
  )
  const [productId, setProductId] = useState(props.id ? props.id : '')
  const [status, setStatus] = useState(props.status ? props.status : '')
  const [editStatus, setEditStatus] = useState(
    props.editStatus ? props.editStatus : ''
  )
  const productNew = useSelector(
    (state) => state.productOperations.productNew.data
  )
  const product = useSelector((state) => state.productOperations.product.data)
  console.log('productNew', productNew)
  console.log('product', product)
  useEffect(() => {
    setStatus(props.status)
  }, [props])
  useEffect(() => {
    if (productNew && productNew.statusCode === 201) {
      props.history.push('/product/update-listing', { record: productNew.data })
      console.log('props', props.history)
    }
  }, [productNew])

  const onFinish = (values) => {
    const { name, category, subCategory, tag, subTitle } = values
    const payload = {}
    payload.name = name
    payload.category = category
    payload.subCategory = subCategory
    payload.tag = tag
    payload.subTitle = subTitle
    payload.status = status
    if (editStatus === '0') {
      dispatch(productCreate(payload))
    }
    if (editStatus === '1') {
      dispatch(productUpdate(productId, payload))
    }
    console.log('values', values)
    console.log('payload', payload)
  }
  return (
    <Form
      labelCol={{ span: 4 }}
      wrapperCol={{ span: 14 }}
      layout="horizontal"
      size="large"
      onFinish={onFinish}
      // requiredMark={requiredMark}
      // initialValues={{
      //   name: '123',
      //   subTitle: '21341234',
      //   category: '254',
      //   tag: '213456',
      // }}
    >
      <Row>
        <Col span={4} style={{ textAlign: 'right' }}>
          <h2>Basic Info </h2>
        </Col>
      </Row>
      <Row>
        <Col span={4} />
        <Col span={14} style={{ textAlign: 'left', color: '#C0C0C0' }}>
          <p style={{ marginBottom: '8px' }}>
            Name your event and tell event-goers why they should come. Add
            details that highlight what makes it unique.
          </p>
        </Col>
      </Row>

      <Item
        name="name"
        label="Listing Name"
        rules={[
          {
            required: true,
            whitespace: true,
            message:
              'Please enter a valid name, cannot be empty, minimum length is 4',
          },
          {
            min: 4,
            message: 'Please enter a valid name,  minimum length is 4',
          },
        ]}
        initialValue={editStatus === '1' ? `${productData.name}` : null}
      >
        <Input placeholder="Be clear and descriptive." />
      </Item>
      <Item
        name="subTitle"
        label="Sub Title"
        initialValue={editStatus === '1' ? `${productData.subTitle}` : null}
      >
        <Input placeholder="Be clear and descriptive." />
      </Item>
      <Row>
        <Col span={4} />
        <Col span={14} style={{ textAlign: 'left', color: '#C0C0C0' }}>
          <p style={{ marginBottom: '8px' }}>
            Choose a primary category to make it easier for customers to find
            you
          </p>
        </Col>
      </Row>
      <Item
        label="Primary Category"
        name="category"
        required
        initialValue={editStatus === '1' ? `${productData.category}` : null}
      >
        <Select placeholder="Choose a primary category">
          <Option value="demo1">Demo 1</Option>
          <Option value="demo2 ">Demo 2</Option>
          <Option value="demo3">Demo 3</Option>
        </Select>
      </Item>
      <Row>
        <Col span={4} />
        <Col span={14} style={{ textAlign: 'left', color: '#C0C0C0' }}>
          <p style={{ marginBottom: '8px' }}>
            Choose a sub category to make it easier for customers to find you
          </p>
        </Col>
      </Row>
      <Item
        label="Sub Category"
        name="subCategory"
        initialValue={editStatus === '1' ? `${productData.subCategory}` : null}
      >
        <Select placeholder="Choose a sub category">
          <Option value="demo1">Demo 1</Option>
          <Option value="demo2 ">Demo 2</Option>
          <Option value="demo3">Demo 3</Option>
        </Select>
      </Item>
      <Row>
        <Col span={4} />
        <Col span={14} style={{ textAlign: 'left', color: '#C0C0C0' }}>
          <p style={{ marginBottom: '8px' }}>
            Improve discoverability of your Listing by adding tags relevant to
            the subject matter
          </p>
        </Col>
      </Row>

      <Item
        name="tag"
        label="Tag"
        rules={[
          {
            min: 4,
            message: 'Please enter a valid name, minimum length is 4',
          },
          {
            max: 14,
            message: 'Please enter a valid name, maximum length is 20',
          },
        ]}
        initialValue={editStatus === '1' ? `${productData.tag}` : null}
      >
        <Input placeholder="Be clear and descriptive." />
      </Item>
      <Row justify="end">
        <Col span={4}>
          <Item>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Item>
        </Col>
      </Row>
    </Form>
  )
}
export default withRouter(BasicInfo)
