/* eslint-disable no-debugger */
import React, { useEffect, useState } from 'react'
import { Popover, Button, Modal } from 'antd'
import { MenuOutlined } from '@ant-design/icons'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { logout } from '../../state/ducks/user/actions'
const title = 'H S'

const Header = (props) => {
  const dispatch = useDispatch()

  // Successfully persist data as userLogin
  // Just use this sample code througout order componenet as needed
  const userLogin = useSelector((state) => state.userOperations.userLogin)
  const userSignIn = useSelector((state) => state.userOperations.userCreate)

  useEffect(() => {
    if (userLogin && userLogin.success === 'success') {
      setLoginVisible(false)
    }
    if (userSignIn && userSignIn.success === 'success') {
      setSignUpVisible(false)
    }
  }, [userLogin, userSignIn])

  const [visible, setVisible] = useState(false)
  const [loginVisible, setLoginVisible] = useState(false)
  const [signUpVisible, setSignUpVisible] = useState(false)
  const onLogin = () => {
    setVisible(!visible)
    setLoginVisible(true)
  }

  const onSignUp = () => {
    setVisible(!visible)
    setSignUpVisible(true)
  }

  const onLogout = () => {
    dispatch(logout(history))
    setVisible(false)
  }

  const handleVisibleChange = (visible) => {
    setVisible(visible)
  }

  const text = <h4>MENU</h4>
  const content = (
    <Menu onLogin={onLogin} onSignUp={onSignUp} onLogout={onLogout} />
  )

  return (
    <>
      <header className="header">
        <Link to="/" className="header-logo">
          <img src="/images/logo1.jpg" alt="logo" />
        </Link>
        <div className="header-menu">
          <Popover
            placement="leftTop"
            title={text}
            content={content}
            visible={visible}
            onVisibleChange={handleVisibleChange}
            trigger="click"
          >
            <Button onClick={() => setVisible(!visible)}>
              <MenuOutlined fill="#000" />
            </Button>
          </Popover>
        </div>
      </header>
      <div className="header-line" />
    </>
  )
}

export default Header
