import React, { useEffect } from 'react'

import { useDispatch, useSelector } from 'react-redux'
import { Col, Row } from 'antd'
import Loader from '../../common/loader/Loader'
import Message from '../../common/Message'
import { userPersonalInfo } from '../../../state/ducks/user/actions'
// import PersonalInfoDetails from '../../features/personalInfo/PersonalInfoDetails'

const PersonalInfo = ({ match }) => {
  const dispatch = useDispatch()

  const userProfile = useSelector(
    (state) => state.userOperations.userPersonalInfo
  )

  const { loading, error, user } = userProfile

  useEffect(() => {
    dispatch(userPersonalInfo())
  }, [dispatch])

  return (
    <>
      <h1>My Profile</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="Error" message={error}>
          {error}
        </Message>
      ) : (
        <h1>{user.data && <h1>{user.data.firstName}</h1>}</h1>
      )}
    </>
  )
}

export default PersonalInfo
