import React from 'react'
import { Table } from 'antd'
import { PAGE_SIZE } from '../../../utils/constants'

const ListTable = (props) => {
  /*
  a.First name 
b.Last name  
c.Email address 
d.Phone number 
e.Gender  
f.Edit/Delete button on each row at the end of the row. 
  */
  const columns = [
    {
      title: 'First name',
      dataIndex: 'firstName',
      sorter: (a, b) => a.firstName.length - b.firstName.length,
      sortDirections: ['descend'],
    },
    {
      title: 'Last name',
      dataIndex: 'lastName',
      sorter: (a, b) => a.lastName.length - b.lastName.length,
      sortDirections: ['descend'],
    },
    {
      title: 'Email',
      dataIndex: 'email',
    },
    {
      title: 'Email address ',
      dataIndex: 'email',
    },
    {
      title: 'Phone number',
      dataIndex: 'tel',
    },
    {
      title: 'Gender',
      dataIndex: 'gender',
      filters: [
        {
          text: 'Male',
          value: 'Male',
        },
        {
          text: 'Female',
          value: 'Female',
        },
      ],
      onFilter: (value, record) => record.gender.indexOf(value) === 0,
    },
  ]
  return (
    <Table
      bordered
      rowKey="_id"
      columns={columns}
      dataSource={props.employees}
      pagination={{ defaultPageSize: PAGE_SIZE }}
    />
  )
}
export default ListTable
